import './App.css';
import React from 'react';


function App() {
  //write a sample app that has 2 fields, with 2 state
  //variables, and a button that when clicked, displays
  //the sum of the 2 fields
  const [x, setX] = React.useState(0);
  const [y, setY] = React.useState(0);
  const [total, setTotal] = React.useState(0);

  const sum = () => {
    fetch('XXXXXXXXXXXXXXXXXXXXXXXXXXXXX', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ x, y })
    })
      .then(res => res.json())
      .then(data => {
        setTotal(data.sum);
      })
  }

  // return a div with 2 fields, on submit call a backend api plus endpoint
  return (
    <div className="App">
      <input type="text" value={x} onChange={(e) => setX(e.target.value)} />
      <input type="text" value={y} onChange={(e) => setY(e.target.value)} />
      <p>{total}</p>
      <button onClick={sum}>Sum</button>
    </div>
  );

}

export default App;
